var pf={entradas:document.querySelectorAll("input.validar"),valor:null,expresionRegular:null,validarPassword:false,validarUsuario:false,validarEmail:false,validarTerminos:false,};var mf={inicioFormulario:function(){for(var a=0;a<pf.entradas.length;a++){pf.entradas[a].addEventListener("focus",mf.enFoco);pf.entradas[a].addEventListener("blur",mf.fueraFoco);pf.entradas[a].addEventListener("change",mf.cambioEntrada)}},enFoco:function(a){pf.valor=a.target.value;if(pf.valor===""){document.querySelector("#"+a.target.id).style.background="rgba(255,255,0,.5)";document.querySelector("[for="+a.target.id+"] .obligatorio").style.opacity=1}document.querySelector("[for="+a.target.id+"]").appendChild(document.createElement("DIV")).setAttribute("class","error")},fueraFoco:function(a){document.querySelector("#"+a.target.id).style.background="white";document.querySelector("[for="+a.target.id+"] .obligatorio").style.opacity=0},cambioEntrada:function(a){pf.valor=a.target.value;if(pf.valor!==""){switch(a.target.id){case"txtNombre":if(pf.valor.length<2||pf.valor.length>6){document.querySelector("[for="+a.target.id+"] .error").innerHTML='<span style="color:red;">Error al ingresar los datos'+a.target.placeholder+"</span>";pf.validarUsuario=false}else{document.querySelector("[for="+a.target.id+"] .error").parentNode.removeChild(document.querySelector("[for="+a.target.id+"] .error"));pf.validarUsuario=true}break;case"txtPassword":pf.expresionRegular=/^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,15}$/;if(!pf.expresionRegular.test(pf.valor)){document.querySelector("[for="+a.target.id+"] .error").innerHTML='<span style="color:red;">Error al ingresar los datos'+a.target.placeholder+"</span>";pf.validarPassword=false}else{document.querySelector("[for="+a.target.id+"] .error").parentNode.removeChild(document.querySelector("[for="+a.target.id+"] .error"));pf.validarPassword=true}break;case"txtEmail":pf.expresionRegular=/^\w+@[a-zA-Z_]+?\.[a-zA-Z]{2,3}$/;if(!pf.expresionRegular.test(pf.valor)){document.querySelector("[for="+a.target.id+"] .error").innerHTML='<span style="color:red;">Error al ingresar los datos'+a.target.placeholder+"</span>";pf.validarEmail=false}else{document.querySelector("[for="+a.target.id+"] .error").parentNode.removeChild(document.querySelector("[for="+a.target.id+"] .error"));pf.validarEmail=true}break;default:}}else{document.querySelector("[for="+a.target.id+"] .error").parentNode.removeChild(document.querySelector("[for="+a.target.id+"] .error"))}},validarFormulario:function(){try{pf.validarTerminos=document.querySelector("#chkTerminos").checked;if(!pf.validarUsuario||!pf.validarPassword||!pf.validarEmail){document.querySelector("#labelEnviar").innerHTML='<span style="color:red;">*tiene errores en los datos, favor revisar de nuevo</span>';return false}else{if(!pf.validarTerminos){document.querySelector("#labelEnviar").innerHTML='<span style="color:red;">*tiene errores en los datos, favor acepte terminos y condiciones</span>';return false}else{return true}}}catch(a){console.log(a);return false}}};mf.inicioFormulario();